import { Component, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {


  show: boolean = true;


  errorMessage!: string;
  loggedIn: boolean = false
  @Output() valueChange = new EventEmitter()
  constructor(
    private authService: AuthService,
    private router: Router,
    private location: Location) { }

  onSubmit() {

    // this.authService.login(this.user.email, this.user.password).subscribe({
    //   next: (v) => {
    //     this.loggedIn = true
    //     this.valueChange.emit(this.user.email)
    //   },
    //   error: (e) => {
    //     if (e.status === 401) {
    //       this.errorMessage = "Invalid credentials"
    //     }

    //   },
    //   complete: () => {

    //   }
    // })
  }

}
<div class="hero">
  <div class="hero-content text-center">
    <div class="w-full max-w-md">
      <h2 class="mb-3 text-3xl font-semibold text-center">Login to your account</h2>
      <p class="text-sm text-center dark:text-gray-400">Dont have account?
        <a [routerLink]="['/signup']" rel="noopener noreferrer" class="focus:underline hover:underline">Sign up here</a>
      </p>
      <div class="my-6 space-y-4">
        <button aria-label="Login with Google" type="button"
          class="flex items-center justify-center w-full p-4 space-x-4 border rounded-md focus:ring-2 focus:ring-offset-1 dark:border-gray-400 focus:ring-violet-400">
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 32 32" class="w-5 h-5 fill-current">
            <path
              d="M16.318 13.714v5.484h9.078c-0.37 2.354-2.745 6.901-9.078 6.901-5.458 0-9.917-4.521-9.917-10.099s4.458-10.099 9.917-10.099c3.109 0 5.193 1.318 6.38 2.464l4.339-4.182c-2.786-2.599-6.396-4.182-10.719-4.182-8.844 0-16 7.151-16 16s7.156 16 16 16c9.234 0 15.365-6.49 15.365-15.635 0-1.052-0.115-1.854-0.255-2.651z">
            </path>
          </svg>
          <p>Login with Google</p>
        </button>
      </div>
      <div class="flex items-center w-full my-4">
        <hr class="w-full dark:text-gray-400">
        <p class="px-3 dark:text-gray-400">OR</p>
        <hr class="w-full dark:text-gray-400">
      </div>
      <form (ngSubmit)="onSubmit()" #loginForm="ngForm" class="space-y-8 ng-untouched ng-pristine ng-valid">
        <div class="space-y-4">
          <div class="space-y-2">
            <div class="flex justify-between">
              <label for="password" class="text-sm">Email address</label>
            </div>
            <input [ngClass]="{
              'input-error': emailInput.invalid  && (emailInput.dirty || emailInput.touched),
              'input-success': emailInput.valid && (emailInput.dirty || emailInput.touched)
             }" type="email" name="email" id="email" placeholder="Enter email here" required pattern="[^ @]*@[^ @]*"
              [(ngModel)]="user.email" #emailInput="ngModel" class="w-full max-w-xs input input-bordered">

            <div class="alert alert-error shadow-lg"
              *ngIf="emailInput.errors && (emailInput.dirty || emailInput.touched)">
              <div>
                <svg xmlns="http://www.w3.org/2000/svg" class="stroke-current flex-shrink-0 h-6 w-6" fill="none"
                  viewBox="0 0 24 24">
                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2"
                    d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <span *ngIf="emailInput.errors['required']">Email is required</span>
                <span *ngIf="emailInput.errors['pattern']">Please enter a valid email address</span>
              </div>
            </div>

          </div>
          <div class="space-y-2">
            <div class="flex justify-between">
              <label for="password" class="text-sm">Password</label>

            </div>
            <input [ngClass]="{
              'input-error': passwordInput.invalid  && (passwordInput.dirty || passwordInput.touched),
              'input-success': passwordInput.valid && (passwordInput.dirty || passwordInput.touched)
             }" type="password" name="password" id="password" placeholder="Enter password here"
              required minlength="8" [(ngModel)]="user.password" #passwordInput="ngModel"
              class="w-full max-w-xs input input-bordered">

            <div class="alert alert-error shadow-lg"
              *ngIf="passwordInput.errors && (passwordInput.dirty || passwordInput.touched)">
              <div>
                <svg xmlns="http://www.w3.org/2000/svg" class="stroke-current flex-shrink-0 h-6 w-6" fill="none"
                  viewBox="0 0 24 24">
                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2"
                    d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <span *ngIf="passwordInput.errors['required']">Password is required</span>
                <span *ngIf="passwordInput.errors['minlength']">Password must be at least 8
                  long</span>
              </div>
            </div>


          </div>
          <a rel="noopener noreferrer" href="#" class="text-xs hover:underline dark:text-gray-400">Forgot
            password?</a>
        </div>
        <button type="submit" [disabled]="loginForm.invalid" class="w-full font-semibold btn btn-primary">Sign
          in</button>
        <div class="alert alert-error shadow-lg" *ngIf="errorMessage">
          <div>
            <svg xmlns="http://www.w3.org/2000/svg" class="stroke-current flex-shrink-0 h-6 w-6" fill="none"
              viewBox="0 0 24 24">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2"
                d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span>{{ errorMessage }}</span>
          </div>
        </div>

      </form>
    </div>

  </div>
</div>
